//         return (
//             <img src={thumbnail} alt="Random character" style={{objectFit:"contain"}} className="randomchar__img"/>
//         )
//     } else {
//         return (
//             <img src={thumbnail} alt="Random character" className="randomchar__img"/>
//         )
//     }
