import './comicsList.scss';
import useMarvelService from '../../services/MarvelService';
import { useEffect, useState } from 'react';
import Spinner from '../spinner/Spinner';
import ErrorMessage from '../errorMessage/ErrorMessage';

const ComicsList = () => {

    const [comicsList, setComicsList] = useState([]);
    const [offset, setOffset ] = useState(1000);
    const [newItemLoading, setNewItemLoading] = useState(false);
    const [comicsEnded, setComicsEnded] = useState(false);

    const {loading, error, getAllComics} = useMarvelService();

    useEffect(() => {
        onRequest(offset, true);
    },[])

    const onRequest = (offset, initial) => {
        initial ? setNewItemLoading(false) : setNewItemLoading(true);
        getAllComics(offset)
            .then(onComicsListLoaded);
    }

    const  onComicsListLoaded = (newComicsList) => {
        let ended = false;
        if (newComicsList.length < 8) {
            ended = true;
        }
        setNewItemLoading(newItemLoading => false);
        setComicsList(comicsList => [...comicsList, ...newComicsList]); 

        setOffset(offset => offset + 8);
        setComicsEnded(comicsEnded => ended);
        // (newComicsList) => [...comicsList, ...newComicsList]
    }

    const spinner = loading && !newItemLoading ? <Spinner/> : null;
    const errorMessage = error ? <ErrorMessage/> : null;

    return (
        <div className="comics__list">
            <ul className="comics__grid">
                {errorMessage}
                {spinner}
                <Items comicsList={comicsList}/>
            </ul>
            <button 
                className="button button__main button__long"
                style={{'display': comicsEnded ? 'none' : 'block'}}
                onClick={() => onRequest(offset)}
                disabled={newItemLoading}>
                <div className="inner">load more</div>
            </button>
        </div>
    )
}

const Items = (props) => {

    // console.log(props, "props");
    const createItem = (id, title, thumbnail, price, url) => {
        
        const realPrice = price === 0 ? '' : price + '$';
        return (
           
            <li key={id} className="comics__item">
                <a href={url}>
                    <img src={thumbnail} alt="ultimate war" className="comics__item-img"/>
                    <div className="comics__item-name">{title}</div>
                    <div className="comics__item-price">{realPrice}</div>
                </a>
            </li>
        )
    }

    return (
        props.comicsList.map((item, i) => {
            const {id, title, thumbnail, price, url} = item;
            return createItem(id, title, thumbnail, price, url);
        })
    )

}

export default ComicsList;
