import ErrorMessage from "../errorMessage/ErrorMessage";
import {Link, useNavigate} from 'react-router-dom';
import { useEffect } from "react";


const Page404 = () => {
    const history = useNavigate();
    useEffect(() => {
        updatePath();
    },[])

    const updatePath = () => {
       history(-1) ? history(-1) : history("/");
    }

    return (
        <div style={{display: 'flex', justifyContent: 'center', flexDirection: 'column', alignItems: 'center'}}>
            <ErrorMessage/>
            <p style={{textAlign: 'center', fontWeight: 'bold', fontSize: '24px'}}> This page not found </p>
            <button style={{marginTop: '30px', width: '200px', backgroundColor: 'lightblue'}}>
                <Link style={{padding: '5px',  width: 'auto', textAlign: 'center', fontWeight: 'bold', fontSize: '24px'}} to={history(-1)}>Back to main page</Link>
            </button>
        </div>
    )
}

export default Page404;
